import { Component } from '@angular/core';
import { Jogo } from './models/jogo';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'loja-games-front';

  jogos!: Jogo[];

  constructor(){
    this.jogos = [];
    this.jogos.push(new Jogo(1, "God of War", "ZUUUUUUUUUUS", 159.99, "https://minutomais.com/wp-content/uploads/2022/08/Capa-Revelada-God-of-War-Ragnarok.jpg"))
    this.jogos.push(new Jogo(2, "Metal Slug", "Explosão", 49.99, "https://t.ctcdn.com.br/gqrrYXNcOCMXM70c4pksRjQwVfU=/78x0:1357x720/1200x675/smart/filters:format(webp)/i346194.png"))

  }
}

<div class="container d-flex justify-content-center">
  <div class="row" *ngFor="let jogo of jogos">
    <!--Card pelo Bootstrap -->
    <div class="card" style="width: 18rem;">
      <img src="" class="card-img-top" alt="">
      <div class="card-body">
        <h5 class="card-title"></h5>
        <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
        <div class="container d-flex justify-content-center">
          <span class="badge badge-pill badge-success p-2 mr-3">199.99</span> <!-- Mr = Margin right -->
          <a href="#" class="btn btn-primary">Comprar</a>
        </div>
      </div>
    </div>


  </div>
</div>
