import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'loja-games-front';
}

<div class="container d-flex justify-content-center">
  <div class="row">


    <!--Card pelo Bootstrap -->
    <div class="card" style="width: 18rem;">
      <img src="https://minutomais.com/wp-content/uploads/2022/08/Capa-Revelada-God-of-War-Ragnarok.jpg" class="card-img-top" alt="God of War">
      <div class="card-body">
        <h5 class="card-title">God of War</h5>
        <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
        <span class="badge badge-pill badge-success p-2 mr-3">199.99</span> <!-- Mr = Margin right -->
        <a href="#" class="btn btn-primary bc: black">Comprar</a>
      </div>
    </div>


  </div>
</div>
