import { Component, OnInit } from '@angular/core';
import { Cosmetico } from 'src/app/models/cosmetico';
import { CosmeticoService } from 'src/app/services/cosmetico.service';

@Component({
  selector: 'app-listagem-cosmeticos',
  templateUrl: './listagem-cosmeticos.component.html',
  styleUrls: ['./listagem-cosmeticos.component.css']
})
export class ListagemCosmeticosComponent implements OnInit{
  public cosmeticos!: Cosmetico[];

  constructor(private cosmeticoService: CosmeticoService){

  }
  ngOnInit(): void {
    this.pegarCosmeticos();
  }

  public pegarCosmeticos(){
    this.cosmeticoService.listagemCosmeticos().subscribe((response) => {
      this.cosmeticos = response;
    })
  }
}

<div class="contener d-flex justify-content-center">
  <div class="row mg" *ngFor="let cosmetico of cosmeticos">

    <div class="card" style="width: 18rem;">
      <div class="card-body">
        <h5 class="card-title">{{ cosmetico.nome }}</h5>
        <a href="#" class="btn btn-primary">Detalhar</a>
      </div>
    </div>

  </div>
</div>
